INSERT INTO `partners` (`id`, `nama`, `foto`, `isi`, `tgl_isi`) VALUES
(4, 'Mashable', 'partners_1657590689_m1.jpg', '<span style=\"color: rgb(77, 81, 86); font-family: arial, sans-serif; font-size: 14px;\">Mashable adalah situs web media digital yang didirikan oleh Pete Cashmore pada tahun 2005. Mashable dirintis oleh Pete Cashmore di rumahnya di Aberdeen, Skotlandia, pada bulan Juli 2005. Time mengakui Mashable sebagai satu dari 25 blog terbaik di Internet pada tahun 2009</span>', '2022-07-12 02:22:41'),
(5, 'khan.academy', 'partners_1657590666_k1.jpg', '<span style=\"color: rgb(77, 81, 86); font-family: arial, sans-serif; font-size: 14px;\">Khan Academy adalah organisasi pendidikan non-profit yang didirikan pada 2006 oleh Sal Khan, lulusan MIT dan Harvard Business Schoo</span>', '2022-07-12 02:19:26'),
(6, 'facebook', 'partners_1657590636_f1.png', '<p><span style=\"font-weight: bold; color: rgb(95, 99, 104); font-family: arial, sans-serif; font-size: 14px;\">Facebook adalah</span><span style=\"color: rgb(77, 81, 86); font-family: arial, sans-serif; font-size: 14px;\">&nbsp;media sosial dan layanan jejaring sosial online Amerika yang dimiliki oleh Meta Platforms. Didirikan pada tahun 2004 oleh Mark Zuckerberg&nbsp;</span><br></p>', '2022-07-12 02:20:22'),
(7, 'change.org', 'partners_1657590616_c1.png', '<span style=\"color: rgb(77, 81, 86); font-family: arial, sans-serif; font-size: 14px;\">hange.org adalah sebuah situs web petisi yang dioperasikan oleh Change.org, Inc., sebuah Perusahaan bersertifikat B Amerika yang diklaim memiliki lebih dari 100 juta pemakai dan mentuanrumahi kampanye-kampanye yang disponsori oleh organisasi-organisasi.</span>', '2022-07-12 02:21:15');

